If anyone is interested, this is an example of how to be notified of session locking/unlocking using Go

```go
package main

import (
	"github.com/godbus/dbus/v5"
	"log"
)

func main() {
	conn, err := dbus.ConnectSystemBus()
	if err != nil {
		log.Fatalln("Failed to connect to session bus:", err)
	}
	defer conn.Close()

	if err = conn.AddMatchSignal(
		// dbus.WithMatchObjectPath("/org/freedesktop/login1/session/_xx"), // Specific session
		dbus.WithMatchPathNamespace("/org/freedesktop/login1/session"), // All sessions
		dbus.WithMatchInterface("org.freedesktop.DBus.Properties"),
		dbus.WithMatchSender("org.freedesktop.login1"),
		dbus.WithMatchMember("PropertiesChanged"),
	); err != nil {
		panic(err)
	}

	c := make(chan *dbus.Signal)
	conn.Signal(c)
	for v := range c {
		changedProperties := v.Body[1].(map[string]dbus.Variant)
		lockedHintProperty, hasLockedHint := changedProperties["LockedHint"]
		if !hasLockedHint {
			continue
		}

		isLocked := lockedHintProperty.Value().(bool)
		if isLocked {
			log.Printf("System is now locked")
		} else {
			log.Printf("System is now unlocked")
		}
	}
}
```

