So, what is the accepted way of mentioning a custom GTKMM widget in a .ui file without `BuilderScope`? I tried the following:
```cpp
// in a .h file
class MyWidget_Class : public Glib::Class {
public:
    const Glib::Class & init();
};

class MyWidget : public Gtk::Widget {
public:
    static GType get_type() G_GNUC_CONST;

private:
    static MyWidget_Class my_widget_class_;
};

// in a .cpp file
MyWidget_Class MyWidget::my_widget_class_;

const Glib::Class &MyWidget_Class::init() {
    if (!gtype_) {
        auto base_query = GTypeQuery{};
        g_type_query(Gtk::Widget::get_type(), &base_query);

        auto derived_info = GTypeInfo{
            .class_size     = (guint16)base_query.class_size
          , .base_init      = nullptr
          , .base_finalize  = nullptr
          , .class_init     = class_init_func_
          , .class_finalize = nullptr
          , .class_data     = nullptr
          , .instance_size  = (guint16)base_query.instance_size
          , .n_preallocs    = 0
          , .instance_init  = nullptr
          , .value_table    = nullptr
          };

        g_type_register_static(Gtk::Widget::get_type(), "MyWidget", &derived_info, GTypeFlags(0));
    }

    return *this;
}

GType MyWidget::get_type() {
    return my_widget_class_.init().get_type();
}

MainWindow * MainWindow::create() {
    g_type_ensure(MyWidget::get_type());

    auto builder = Gtk::Builder::create_from_resource("/main-window.ui");
    auto window  = Gtk::Builder::get_widget_derived<MainWindow>(builder, "main-window");
    }
    return window;
}
